#include <vector>
#include <queue>
#include <chrono>
#include <iostream>

/**
 * 934. Shortest Bridge
 *
 * You are given an n x n binary matrix grid where 1 represents land and 0 represents water.
 * An island is a 4-directionally connected group of 1's not connected to any other 1's. There are exactly two islands in grid.
 * You may change 0's to 1's to connect the two islands to form one island.
 * Return the smallest number of 0's you must flip to connect the two islands.
 */

class Solution {
private:
	static inline const int dX[] = {0, 1, 0, -1};
	static inline const int dY[] = {1, 0, -1, 0};

public:
	static int shortestBridge(std::vector<std::vector<int>>& grid) {
		const int n = grid.size();
		std::queue<int> q, r;

		std::vector<bool> vis(n * n);
		// tag 1st island
		for (int i = 0; i < n * n; ++i) {
			if (grid[i / n][i % n]) {
				q.push(i);
				vis[i] = true;
				while (not q.empty()) {
					int c = q.front();
					q.pop();
					grid[c / n][c % n] = -1;
					r.push(c);
					r.push(0);
					for (int j = 0; j < 4; ++j) {
						const int nX = c / n + dX[j], nY = c % n + dY[j];
						if (nX >= 0 && nX < n && nY >= 0 && nY < n && grid[nX][nY] == 1 && !vis[nX * n + nY]) {
							q.push(nX * n + nY);
							vis[nX * n + nY] = true;
						}
					}
				}
				break;
			}
		}

		while (!r.empty()) {
			int c = r.front();
			r.pop();
			int l = r.front();
			r.pop();

			for (int j = 0; j < 4; ++j) {
				const int nX = c / n + dX[j], nY = c % n + dY[j];
				if (nX < 0 || nX >= n || nY < 0 || nY >= n)
					continue;

				int x = nX * n + nY;
				if (!vis[x] && grid[nX][nY] != -1) {
					if (grid[nX][nY] == 1)
						return l;
					r.push(x);
					r.push(1 + l);
					vis[x] = true;
				}
			}
		}

		return -1;
	}
};

